window.addEventListener("DOMContentLoaded",()=>{const a=new IntersectionObserver(a=>{a.forEach(a=>{const b=a.target.getAttribute("id");a.isIntersecting?document.querySelector(`nav li a[href="#${b}"]`).parentElement.classList.add("active"):document.querySelector(`nav li a[href="#${b}"]`).parentElement.classList.remove("active")})});document.querySelectorAll("h1[id],h2[id],h3[id]").forEach(b=>{a.observe(b)})})